import axios from "axios";

export default {

  // Saves a contact info and messaege to the database
  saveLead: function(leadData) {
    return axios.post("http://localhost:3000/agavepv/contact/lead", leadData);
  },

  sendMail: function(mailData) {
    return axios.post("http://localhost:3000/agavepv/contact/mail", mailData);
  },

  scrapeArticles: function() {
    return axios.get("http://localhost:3000/agavepv/scrape", (request, response) => {
      console.log(`Response frontend: ${response}`)
    })
  }
};
